mapper.select:
select customer_lottery.*, user.recharge_points, user.number_winners
from customer_lottery
left join user on user.id = customer_lottery.user_id
where customer_lottery.lottery_id = #{lotteryId}
order by customer_lottery.points desc
limit #{page},#{size}

mapper.update:
update customer_lottery
set is_designated = #{isDesignated}
where id = #{id}
